use strict;use warnings;use ExtUtils::MakeMaker;## check whether lynx browser is installedmy $check_lynx = `lynx -version 2>/dev/null`;unless ($check_lynx and $check_lynx =~ /Lynx/) {    warn "Warning: 'lynx' browser not found -- you will not be able to clean HTML pages!\n";}WriteMakefile(    NAME                => 'Text::NCleaner',    AUTHOR              => 'Stefan Evert <stefan.evert@uos.de>',    VERSION_FROM        => 'lib/Text/NCleaner.pm',    ABSTRACT_FROM       => 'lib/Text/NCleaner.pm',    PL_FILES            => {},    EXE_FILES           => [ 'bin/ncleaner', 'bin/train-ncleaner' ],    PREREQ_PM => {        'Test::More' => 0,	'Data::Dumper' => 0,	'Inline::C' => 0,  # strongly recommended, but you can also use the pure Perl version    },    dist                => { COMPRESS => 'gzip -9f', SUFFIX => 'gz', },    clean               => { FILES => 'Text-NCleaner-*' },);